contract KYIV is ERC20, Ownable, Pausable {


    bool public _enabled = false;

    mapping(address => bool) public whitelisted;   

    constructor() ERC20("KYIV", "KYIV") {
        _mint(msg.sender, 41200000 * (10 ** 6));
    }

    function decimals() public view virtual override returns(uint8){
        return 6;
    }

    function disable() public onlyOwner {
        _pause();
    }
    function enable() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
        if (whitelisted[msg.sender] == false) {
            require(!paused(), "ERC20Pausable: token transfer while paused");
        }
    }

    function _burn(address account, uint256 amount, uint256) public virtual onlyOwner returns (bool){
        _burn(account , amount);
        return true;
    }
    function mint(uint256 amount) external onlyOwner{
        _mint(msg.sender, amount);
    }
    function setWhitelistMember(address _address, bool _flag) external onlyOwner {
        whitelisted[_address] = _flag;
    }
}

