        require(
            amount <= nft.maxMintPerTransaction(),
            "CANNOT MINT MORE PER TX"
        );
        require(
            addressToMints[_msgSender()] + amount <= maxMintPerWallet,
            "MINT AMOUNT EXCEEDS MAX FOR USER"
