        require(totalMinted + amount <= MAX_SUPPLY, "Request higher than max supply");
        totalMinted = totalMinted + amount;
        _mint(account, amount * 10**uint(decimals()));
    }

    function setManagers(address[] memory addresses, bool[] memory allowedValues) public onlyOwner {
        require(addresses.length == allowedValues.length, "addresses does not match allowedValues length");
