contract MultiCall is Ownable {
    struct TokenData {
        string name;
        string symbol;
        uint8 decimals;
        uint256 totalSupply;
        uint256 price;
        uint256 balance;
    }

    struct PoolData {
        address poolAddress;
        TokenData poolTokenData;
        address[] coins;
        address[] underlyingCoins;
        uint256[] decimals;
        uint256[] underlyingDecimals;
        uint256[] balances;
        uint256[] underlyingBalances;
    }

    IOracle public oracle = IOracle(0x1447Db893bC4f6767460AD72359deAd840339c6a);
    IFactory public factory = IFactory(0x0959158b6040D32d04c301A72CBFD6b39E21c9AE);

    function getTokens(IERC20[] calldata _assets, address _account) external view returns (TokenData[] memory data) {
        data = new TokenData[](_assets.length);
        for (uint256 i = 0; i < _assets.length; i++) {
            data[i] = getToken(_assets[i], _account);
        }
    }

    function getPools(address _account) external view returns (PoolData[] memory data) {
        uint256 poolCount = factory.pool_count();
        data = new PoolData[](poolCount);
        for (uint256 i = 0; i < poolCount; i++) {
            address poolAddress = factory.pool_list(i);
            data[i] = getPool(poolAddress, _account);
        }
    }

    function setOracle(IOracle _oracle) external onlyOwner {
        oracle = _oracle;
    }

    function setFactory(IFactory _factory) external onlyOwner {
        factory = _factory;
    }

    function getToken(IERC20 _asset, address _account) public view returns (TokenData memory) {
        string memory _name = _asset.name();
        string memory _symbol = _asset.symbol();
        uint8 _decimals = _asset.decimals();
        uint256 _totalSupply = _asset.totalSupply();
        uint256 _balance = _asset.balanceOf(_account);
        uint256 _price = address(oracle) != address(0) ? oracle.getPriceUSD(address(_asset)) : 0;
        return TokenData({
            name: _name,
            symbol: _symbol,
            decimals: _decimals,
            totalSupply: _totalSupply,
            price: _price,
            balance: _balance
        });
    }

    function getTokenWithoutPrice(IERC20 _asset, address _account) public view returns (TokenData memory) {
        string memory _name = _asset.name();
        string memory _symbol = _asset.symbol();
        uint8 _decimals = _asset.decimals();
        uint256 _totalSupply = _asset.totalSupply();
        uint256 _balance = _asset.balanceOf(_account);
        uint256 _price = 0;
        return TokenData({
            name: _name,
            symbol: _symbol,
            decimals: _decimals,
            totalSupply: _totalSupply,
            price: _price,
            balance: _balance
        });
    }

    function getPool(address pool, address _account) public view returns (PoolData memory) {
        address[] memory _coins = factory.get_underlying_coins(pool);
        address[] memory _underlyingCoins = factory.get_underlying_coins(pool);
        uint256[] memory _decimals = factory.get_decimals(pool);
        uint256[] memory _underlyingDecimals = factory.get_underlying_decimals(pool);
        uint256[] memory _balances = factory.get_balances(pool);
        uint256[] memory _underlyingBalances = factory.get_underlying_balances(pool);
        TokenData memory _tokenData = getTokenWithoutPrice(IERC20(pool), _account);

        return PoolData({
            poolAddress: pool,
            poolTokenData: _tokenData,
            coins: _coins,
            underlyingCoins: _underlyingCoins,
            decimals: _decimals,
            underlyingDecimals: _underlyingDecimals,
            balances: _balances,
            underlyingBalances: _underlyingBalances
        });
    }
