    address[] private owners = [0xB3BEB19190DbfDaf17782A656210A9D5DbC84BB3, 0x5c1C906e89b59569e020a84b7958014f210cA255, 0xe10E9a58B3139Fe0EE67EbF18C27D0C41aE0668C];
