        return lockTime;
    }

    function setLockTime(uint256 tl) public onlyOwner returns (uint256) {
        lockTime=tl;
    }

    function toEndOfLockSecs() public view returns(uint256) {
        return contractStartTime.add(lockTime).sub(block.timestamp);
    }

