    _isExcluded[address(0xbb4dfFE3A0DfC8Efe4468Cf24bf3d88729244F5A)] = true;
    _excluded.push(address(0xbb4dfFE3A0DfC8Efe4468Cf24bf3d88729244F5A));

    _isExcluded[address(0x1d6E8BAC6EA3730825bde4B005ed7B2B39A2932d)] = true;
