    warden = msg.sender;
    MAXIMUM_SUPPLY = 1_000_000_000;
  }

  /// ::::::::::::::::::::::  MODIFIERS  :::::::::::::::::::::: ///

  modifier canMint() {
    if (mintable[msg.sender] - minted[msg.sender] == 0) {
      revert MintCapacityReached();
    }
