  function mint(address to, uint256 value) public virtual canMint {
    minted[msg.sender] += value;
    _mint(to, value);
  }

  /// ::::::::::::::::::::::  PRIVILEGED  :::::::::::::::::::::: ///

  function setMintable(address minter, uint256 amount) public onlyWarden {
