contract CECMigrator is Initializable {
    using SafeMathUpgradeable for uint256;

    address public KUN;
    address public CEC;
    uint256 public constant RATIO = 10;
    uint256 public constant DURATION = 180 days;

    event Migrated(address indexed sender, uint256 indexed amount);
    event Claimed(address indexed sender, uint256 indexed amount);

    struct Stake {
        uint256 start;
        uint256 end;
        uint256 amount;
    }

    uint256 public totalStake;
    mapping(address => uint256) public stakes;
    mapping(address => Stake[]) public stakeLists;
    mapping(address => mapping(uint256 => uint256)) public claimed;

    function initialize(address _KUN, address _CEC) public initializer {
        KUN = _KUN;
        CEC = _CEC;
    }

    function migrate(uint256 amount) external {
        require(amount > 0, "!amount");
        totalStake = totalStake.add(amount);
        stakes[msg.sender] = stakes[msg.sender].add(amount);
        stakeLists[msg.sender].push(
            Stake(now, now + DURATION, amount.mul(RATIO))
        );
        IERC20Burnable(KUN).burnFrom(msg.sender, amount);
        emit Migrated(msg.sender, amount);
    }

    function claimable(address account, uint256 i)
        public
        view
        returns (uint256)
    {
        Stake[] memory stakeList = stakeLists[account];
        if (i >= stakeList.length) return 0;
        uint256 timeDiff = MathUpgradeable.min(
            now - stakeList[i].start,
            stakeList[i].end - stakeList[i].start
        );
        return
            timeDiff.mul(stakeList[i].amount).div(DURATION).sub(
                claimed[account][i]
            );
    }

    function claim() external {
        uint256 claimAmount = 0;
        Stake[] memory stakeList = stakeLists[msg.sender];
        for (uint256 i = 0; i < stakeList.length; ++i) {
            uint256 claimAmountPerStake = claimable(msg.sender, i);
            claimAmount = claimAmount.add(claimAmountPerStake);
            claimed[msg.sender][i] = claimed[msg.sender][i].add(
                claimAmountPerStake
            );
        }
        require(claimAmount > 0, "!claim");
        IERC20Burnable(CEC).mint(msg.sender, claimAmount);
        emit Claimed(msg.sender, claimAmount);
    }

    function stakeDetails(address account)
        external
        view
        returns (bytes[] memory data)
    {
        Stake[] memory stakeList = stakeLists[account];
        data = new bytes[](stakeList.length);
        for (uint256 i = 0; i < stakeList.length; ++i) {
            data[i] = abi.encode(
                stakeList[i].start,
                stakeList[i].end,
                stakeList[i].amount,
                stakeList[i].amount.sub(claimed[account][i]),
                claimable(account, i)
            );
        }
    }
