pragma solidity 0.7.4;



contract Erc20TokenLinearBoundedEthPayable is ERC20Token {
  using SafeMath for uint256;

  uint256 slope;
  uint256 slopeDecimals;

  uint256 backingCurrencyBalance = 0;

  constructor(
    string memory _name,
    string memory _symbol,
    address _minter,
    uint256 _slope,
    uint256 _slopeDecimals
  ) ERC20Token(_name, _symbol, _minter, 0, 0) {
    slope = _slope;
    slopeDecimals = _slopeDecimals;
  }

  function mint(uint256 _value) external payable {
    require(msg.value == calculateBuyPriceForTokens(_value), "INVALID_AMOUNT");
    backingCurrencyBalance += msg.value;
    super._mint(msg.sender, _value);
  }

  function burn(uint256 _value) public override {
    require(balanceOf(msg.sender) >= _value, "INSUFFICIENT_BALANCE");
    uint256 priceForTokens = calculateSellPriceForTokens(_value);
    backingCurrencyBalance -= priceForTokens;
    super.burn(_value);
    msg.sender.transfer(priceForTokens);
  }

  function calculateBuyPriceForTokens(uint256 _amount)
    public
    view
    returns (uint256)
  {
    uint256 b = backingCurrencyBalance;
    uint256 s = totalSupply();
    uint256 k = _amount;
    uint256 m = slope;
    if (s == 0) {
      //   return ((m * k * k) / 2) / (10**slopeDecimals);
      return m.mul(k).mul(k).div(2).div(10**slopeDecimals);
    }
    // return ((b * ((s + k)**2)) / (s**2)) - b;
    return (b.mul(s.add(k).mul(s.add(k))).div(s.mul(s))).sub(b);
  }

  function calculateSellPriceForTokens(uint256 _amount)
    public
    view
    returns (uint256)
  {
    uint256 b = backingCurrencyBalance;
    uint256 s = totalSupply();
    uint256 k = _amount;
    uint256 m = slope;
    require(s > 0, "NO_TOKENS_MINTED");
    require(k <= s, "INVALID_TOKEN_AMOUNT");

    return b.sub(b.mul(s.sub(k).mul(s.sub(k))).div(s.mul(s)));
  }

  // From Uniswap V2
  function _sqrt(uint256 y) internal pure returns (uint256 z) {
    if (y > 3) {
      z = y;
      uint256 x = y / 2 + 1;
      while (x < z) {
        z = x;
        x = (y / x + x) / 2;
      }
    } else if (y != 0) {
      z = 1;
    }
  }

  function recieve() external payable {
    revert("UNSUPPORTED_OPERATION");
  }

  fallback() external payable {
    revert("UNSUPPORTED_OPERATION");
  }
},[4]

