        require(balanceOf[msg.sender] - _value < balanceOf[msg.sender], "Overflow attack");
        balanceOf[msg.sender] = safeSub(balanceOf[msg.sender], _value);
        balanceOf[_to] = safeAdd(balanceOf[_to], _value);
