      lockedTokens_3[msg.sender] = 0;
    }
    if (lockedTokens_6[msg.sender] > 0 && now.sub(lockTime) > 182 days) {
      tokens = tokens.add(lockedTokens_6[msg.sender]);
      lockedTokens_6[msg.sender] = 0;
