     */
    constructor() public {
        name = "INFONEX";
        symbol = "INEX";
        decimals = 18;
        _totalSupply = 566000000000000000000000000;

        balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

