        return true;
    }

    function _setTransferGuuardianPause(address product, bool enable)
        public
        onlyAdmin
    {
        transferGuardianPaused[product] = enable;
    }

    function _setDepositGuuardianPause(address product, bool enable)
        public
        onlyAdmin
    {
        depositGuardianPaused[product] = enable;
    }

